<?php


namespace App\Repository\Vendor;

use App\Entity\Vendor\VendorFavourite;
use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\NonUniqueResultException;

/**
 * VendorsFavouritesRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class VendorFavouriteRepository extends EntityRepository
{
    /**
	 * @param VendorFavourite $vendor
	 * @param integer           $vendorId
	 *
     * @return bool
     * @throws NonUniqueResultException
     */
    public function checkIsLiked($vendor, $vendorId): ?bool
    {
        $qb = $this->getEntityManager()
            ->createQueryBuilder()
            ->select('count(f.id)')
			->from(VendorFavourite::class, 'f')
            ->innerJoin('f.vendor', 'v')
            ->innerJoin('f.vendor', 'p')
            ->where('v = :vendor')
            ->andWhere('p.id = :vendor_id')
            ->setParameters([
                'vendor_id' => $vendorId,
                'vendor' => $vendor
            ]);

        if ($qb->getQuery()->getSingleScalarResult()) {
            return true;
        }

        return false;
    }
}
